import React, { Component } from "react";

export default class Workouts extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false,
        };
    }

    componentDidMount() {
        fetch('http://192.168.50.71:8000/users?userid=eq.' + document.cookie)
        .then(response => { 
            response.json().then(data => {
                if (data[0].isadmin != 1){
                    window.location = "/sign-in"
                }
                else {
                    fetch('http://192.168.50.71:8000/UserInfo').then(result => {
                        result.json().then(data2 => {
                            this.setState({
                                isLoaded: true,
                                items: data2
                            })

                        })               

                    })

                }
            });

        });

    }

    removeUser(userid) {
            // fetch('http://192.168.50.71:8000/UserInfo?name=eq.' + document.getElementById("name").value).then(response => {
            //         response.json().then(data => {
            //         console.log(data)
            //         var userid = data[0].userid;
            fetch('http://192.168.50.71:8000/users?userid=eq.' + userid,
            {
                method: "DELETE",
                headers: {
                  "Content-type": "application/json",
                  "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJyb2xlIjoiYWRtaW5zIn0.IW9X3o4xo5xxOPWE-UE2ZojD4Ee8-26L2wdYvWkm6ao"
                },
                body: JSON.stringify({
                  userid: document.getElementById("remove").value()
                })
              })
            .then(reply => {
                console.log("Added");
            });

            //Add client to database
            //UPDATE public.users SET
            //trainer = '2'::integer WHERE userid = 2;
            //var sql = "UPDATE public.users SET trainer = trainer.userid::integer WHERE userid = userid";
            //var dbConnection = SQL.Connect

        // })
        // })
    }

    render() {
        const { items } = this.state;
        if (!this.state.isLoaded) {
            return <div>Loading ... </div>;
        }
        else {
            return (

                //Fetch workouts by user ID?
                //UserID is passed in from login?
                //Cookie = UserID
                //Display Workouts
                <div>
          {items.map(item => (
            <div key={item.userid}>
              <h3>Name: {item.name}</h3>
              <h3> Email: {item.email}</h3>
              <h3>User Id: {item.role}</h3>
              <button type="submit" className="btn btn-primary btn-block" onClick={this.removeUser(item.userid)}>Remove {item.name}</button>
              <br></br>
            </div>
          ))}
                </div>

            );
        }
    }
}
