            isLoaded: false,
        };
    }

    componentDidMount() {
       // e.preventDefault();
        fetch('http://192.168.50.71:8000/TrainersView?userid=eq.' + document.cookie)
        .then(response => { 
            response.json().then(data => {
                this.setState({
                    isLoaded: true,
                    items: data
                })
                console.log(data);
            });

        });
    }

