import React, { Component } from "react";

export default class Trainers extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false,
        };
    }

    componentDidMount() {
       // e.preventDefault();
        fetch('http://192.168.50.71:8000/TrainersView?userid=eq.' + document.cookie)
        .then(response => { 
            response.json().then(data => {
                this.setState({
                    isLoaded: true,
                    items: data
                })
                console.log(data);
            });

        });
    }

    removeButton() {

        //UPDATE public.users SET trainer = NULL::integer WHERE userid = 2;

    }

    addClient() {
        fetch('http://192.168.50.71:8000/users?userid=eq.' + document.getElementById("userid").value)
        .then(response => {

        })
        //Add client to database
        //UPDATE public.users SET
        //trainer = '2'::integer WHERE userid = 2;
        //var sql = "UPDATE public.users SET trainer = trainer.userid::integer WHERE userid = userid";
        //var dbConnection = SQL.Connect




    }

    render() {
        const { items } = this.state;
        if (!this.state.isLoaded) {
            return <div>Loading ... </div>;
        }
        else {
            return (

                //Fetch workouts by user ID?
                //UserID is passed in from login?
                //Cookie = UserID
                //Display Workouts
                <div>
                    {items.map(item => (
                        <div key={item.name}>
                            <p> </p>
                            <h3>Client: {item.name}</h3>
                            <h3>UserId: {item.clients}</h3>
                            <button type="submit" className="btn btn-primary btn-block">Remove {item.name}</button>
                        </div>
                    ))}
                    <div>
                        <p> </p>
                        <p> </p>
                    </div>
                    <div>
                        <p>___________________________________________________</p>
                    </div>
                    <div padding-top="100px" >
                        <h3>Add New Client</h3>
                        <div>
                            <p>Client</p>
                            <input type="text" id="name" ></input>
                        </div>
                        <div>
                            <p>UserId</p>
                            <input type="text" id="userid"></input>
                        </div>
                        <button type="submit" className="btn btn-primary btn-block" onClick={this.addClient}>Add Client</button>
                    </div>
             </div>

            );
        }
    }
}
